mapper.insert:
INSERT INTO t_board (
POST_NAME
, POST_WRITER
, POST_CONTENT
, POST_DATE
) VALUES (
#{postName}
, #{postWriter}
, #{postContent}
, NOW()
)

mapper.update:
UPDATE t_board SET
POST_NAME = #{postName}
, POST_WRITER = #{postWriter}
, POST_CONTENT = #{postContent}
, EDIT_DATE = NOW()
WHERE
POST_IDX = #{postIdx}

